contracts/attestation/AttesterStaking.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/// @title AttesterStaking
/// @notice Simple staking/slashing for attesters. Protocol governance controls slashing.
contract AttesterStaking is AccessControl {
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant SLASHER_ROLE = keccak256("SLASHER_ROLE");

    IERC20 public immutable stakeToken;
    uint256 public immutable minStake;

    mapping(address => uint256) public stakeOf;

    event Staked(address indexed attester, uint256 amount);
    event Unstaked(address indexed attester, uint256 amount);
    event Slashed(address indexed attester, uint256 amount, address indexed recipient);

    constructor(address admin, IERC20 stakeToken_, uint256 minStake_) {
        stakeToken = stakeToken_;
        minStake = minStake_;
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(ADMIN_ROLE, admin);
        _grantRole(SLASHER_ROLE, admin);
    }

    function stake(uint256 amount) external {
        require(amount > 0, "amount=0");
        require(stakeToken.transferFrom(msg.sender, address(this), amount), "xfer fail");
        stakeOf[msg.sender] += amount;
        emit Staked(msg.sender, amount);
    }

    function withdraw(uint256 amount) external {
        require(amount > 0, "amount=0");
        uint256 current = stakeOf[msg.sender];
        require(current >= amount, "insufficient");
        uint256 newStake = current - amount;
        require(newStake == 0 || newStake >= minStake, "below min");
        stakeOf[msg.sender] = newStake;
        require(stakeToken.transfer(msg.sender, amount), "xfer fail");
        emit Unstaked(msg.sender, amount);
    }

    function isStaked(address attester) external view returns (bool) {
        return stakeOf[attester] >= minStake;
    }

    function slash(address attester, uint256 amount, address recipient) external onlyRole(SLASHER_ROLE) {
        require(recipient != address(0), "recipient=0");
        uint256 current = stakeOf[attester];
        require(current >= amount, "exceeds");
        stakeOf[attester] = current - amount;
        require(stakeToken.transfer(recipient, amount), "xfer fail");
        emit Slashed(attester, amount, recipient);
    }
}

contracts/automation/AutomationDistribut.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {RedistributionPool} from "../RedistributionPool.sol";

/// @title AutomationDistributor
/// @notice Minimal Chainlink Automation-compatible adapter wrapper for `RedistributionPool.distribute`.
contract AutomationDistributor is AccessControl {
    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");

    RedistributionPool public immutable pool;
    uint64 public immutable minInterval;
    uint256 public batchSize;
    uint64 public lastRun;

    event Performed(uint64 when, uint256 batchSize);

    constructor(address admin, RedistributionPool pool_, uint64 minInterval_, uint256 batchSize_) {
        pool = pool_;
        minInterval = minInterval_;
        batchSize = batchSize_;
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(ADMIN_ROLE, admin);
    }

    function setBatchSize(uint256 newSize) external onlyRole(ADMIN_ROLE) {
        require(newSize > 0, "size=0");
        batchSize = newSize;
    }

    function checkUpkeep(bytes calldata)
        external
        view
        returns (bool upkeepNeeded, bytes memory performData)
    {
        upkeepNeeded = (block.timestamp >= lastRun + minInterval);
        performData = abi.encode(batchSize);
    }

    function performUpkeep(bytes calldata performData) external {
        (uint256 size) = abi.decode(performData, (uint256));
        if (size == 0) size = batchSize;
        pool.distribute(size);
        lastRun = uint64(block.timestamp);
        emit Performed(lastRun, size);
    }
}

contracts/CompensationLimiter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";

/// @title CompensationLimiter
/// @notice Tracks per-identity compensation and enforces an annual cap.
/// @dev Units are in smallest units of the salary token configured by the caller.
contract CompensationLimiter is AccessControl {
    bytes32 public constant CALLER_ROLE = keccak256("CALLER_ROLE");

    struct PeriodData {
        uint64 periodStart;
        uint256 paidInPeriod;
    }

    // identityId => period data
    mapping(bytes32 => PeriodData) private _periods;

    uint256 public immutable annualCap;
    uint256 public immutable periodLength;

    event Consumed(bytes32 indexed identityId, uint256 allowed, uint256 overflow, uint64 newPeriodStart, uint256 paidInPeriod);

    constructor(address admin, uint256 annualCap_, uint256 periodLength_) {
        require(admin != address(0), "admin=0");
        require(annualCap_ > 0, "cap=0");
        require(periodLength_ >= 30 days && periodLength_ <= 400 days, "period invalid");
        annualCap = annualCap_;
        periodLength = periodLength_;
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
    }

    function remainingAllowance(bytes32 identityId) public view returns (uint256 remaining, uint64 periodStart, uint256 paid) {
        PeriodData memory p = _periods[identityId];
        if (p.periodStart == 0) {
            return (annualCap, uint64(block.timestamp), 0);
        }
        if (block.timestamp >= p.periodStart + periodLength) {
            return (annualCap, uint64(block.timestamp), 0);
        }
        if (p.paidInPeriod >= annualCap) {
            return (0, p.periodStart, p.paidInPeriod);
        }
        return (annualCap - p.paidInPeriod, p.periodStart, p.paidInPeriod);
    }

    /// @notice Consumes up to the remaining allowance for an identity; returns (allowed, overflow)
    function consume(bytes32 identityId, uint256 requested) external onlyRole(CALLER_ROLE) returns (uint256 allowed, uint256 overflow) {
        PeriodData memory p = _periods[identityId];
        if (p.periodStart == 0 || block.timestamp >= p.periodStart + periodLength) {
            p.periodStart = uint64(block.timestamp);
            p.paidInPeriod = 0;
        }
        uint256 remaining = annualCap > p.paidInPeriod ? (annualCap - p.paidInPeriod) : 0;
        if (requested <= remaining) {
            allowed = requested;
            overflow = 0;
        } else {
            allowed = remaining;
            overflow = requested - remaining;
        }
        p.paidInPeriod += allowed;
        _periods[identityId] = p;
        emit Consumed(identityId, allowed, overflow, p.periodStart, p.paidInPeriod);
    }
}

contracts/EligibilityRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.26;

import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";
import {EnumerableSet} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

/// @title EligibilityRegistry
/// @notice Maintains identity bindings and eligibility to receive redistribution.
///         Stores only pseudonymous identifiers (identityId) to avoid PII on-chain.
contract EligibilityRegistry is AccessControl {
    using EnumerableSet for EnumerableSet.AddressSet;

    bytes32 public constant ADMIN_ROLE = keccak256("ADMIN_ROLE");
    bytes32 public constant ATTESTER_ROLE = keccak256("ATTESTER_ROLE");
    address public verifier; // optional zk verifier contract

    // address -> identityId (e.g., keccak256 hash of a verifiable credential DID subject)
    mapping(address => bytes32) private _addressToIdentityId;

    // Track addresses currently eligible to receive redistribution
    EnumerableSet.AddressSet private _eligible;

    event IdentityRegistered(address indexed subject, bytes32 indexed identityId);
    event EligibilityGranted(address indexed subject, bytes32 indexed identityId);
    event EligibilityRevoked(address indexed subject, bytes32 indexed identityId);
    event VerifierSet(address indexed verifier);

    constructor(address admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
        _grantRole(ADMIN_ROLE, admin);
    }

    /// @notice Registers or updates the identityId associated with an address.
    /// @dev Only trusted attesters can bind identityIds to addresses.
    function registerIdentity(address subject, bytes32 identityId) external onlyRole(ATTESTER_ROLE) {
        require(subject != address(0), "subject=0");
        require(identityId != bytes32(0), "identityId=0");
        _addressToIdentityId[subject] = identityId;
        emit IdentityRegistered(subject, identityId);
    }

    function getIdentityId(address subject) external view returns (bytes32) {
        return _addressToIdentityId[subject];
    }
    /// @notice Optionally set a ZK verifier contract that validates age/uniqueness proofs.
    function setVerifier(address newVerifier) external onlyRole(ADMIN_ROLE) {
        verifier = newVerifier;
        emit VerifierSet(newVerifier);
    }

    /// @notice Registers identity using an off-chain ZK proof verified by `verifier`.
    /// @dev If a verifier is set, it must return true for the provided `data` (opaque bytes).
    function registerIdentityWithProof(address subject, bytes32 identityId, bytes calldata data) external {
        require(verifier != address(0), "verifier unset");
        (bool ok, bytes memory res) = verifier.staticcall(abi.encodeWithSignature("verify(bytes)", data));
        require(ok && res.length == 32 && abi.decode(res, (bool)), "invalid proof");
        _addressToIdentityId[subject] = identityId;
        emit IdentityRegistered(subject, identityId);
    }


    function isEligible(address subject) external view returns (bool) {
        return _eligible.contains(subject);
    }

    function eligibleCount() external view returns (uint256) {
        return _eligible.length();
    }

    function eligibleAtIndex(uint256 index) external view returns (address) {
        return _eligible.at(index);
    }

    /// @notice Grants eligibility to a single address after ensuring it has an identityId.
    function grantEligibility(address subject) public onlyRole(ATTESTER_ROLE) {
        bytes32 identityId = _addressToIdentityId[subject];
        require(identityId != bytes32(0), "identity not set");
        bool added = _eligible.add(subject);
        require(added, "already eligible");
        emit EligibilityGranted(subject, identityId);
    }

    function grantEligibilityBatch(address[] calldata subjects) external onlyRole(ATTESTER_ROLE) {
        for (uint256 i = 0; i < subjects.length; i++) {
            // Will revert if invalid; keeps state consistent
            grantEligibility(subjects[i]);
        }
    }

    function revokeEligibility(address subject) external onlyRole(ATTESTER_ROLE) {
        bytes32 identityId = _addressToIdentityId[subject];
        require(identityId != bytes32(0), "identity not set");
        bool removed = _eligible.remove(subject);
        require(removed, "not eligible");
        emit EligibilityRevoked(subject, identityId);
    }

    /// @notice Admin can add or remove attesters.
    function setAttester(address attester, bool isAttester) external onlyRole(ADMIN_ROLE) {
        if (isAttester) {
            _grantRole(ATTESTER_ROLE, attester);
        } else {
            _revokeRole(ATTESTER_ROLE, attester);
        }
    }
}

